
public class Ödev5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for döngüsü
		
		for(int i=1;i<10;i++) {
	      System.out.println(i);
			//i++ demek i+1 ile aynı şeydir
	        // i+2 dersek 2şer artar
			
		}
	    System.out.println("Döngü Bitti");
	    
	    
	    
	    
	       //while döngüsü 
	    
	    int i=2;
	    while(i<10) { 
	    	 System.out.println(i);
	    	 i+=2;
	    } 
	     System.out.println("Döngü Bitti");
	    
		//sonsuz olur o yüzden kaçar artacak yazılır
	     


}
}
